import path from "path";

import { BUILD_FOLDER_NAME } from "../../../constants";

export const DEFAULT_FILE_HEADER = (configDir: string) => `/**
 * ⚠️ DO NOT EDIT DIRECTLY - Generated by @mogielski/design-forge
 * 
 * This file is auto-generated by design-forge based on your configuration defined at:
 * ${path.relative(process.cwd(), configDir).replace(/\\/g, "/")}
 */ 
`; // the .replace is to make sure that the path doesn't differ between windows and unix - it gets annoying when it changes every time someone different builds on a project.

export const DEFAULT_EXTERNAL_PATH = "./src/@design";
export const DEFAULT_CONFIG_PATH = `${DEFAULT_EXTERNAL_PATH}/config.ts`;
export const DEFAULT_OUTPUT_PATH = `${DEFAULT_EXTERNAL_PATH}/${BUILD_FOLDER_NAME}`;
export const DEFAULT_PRETTIER_PATH = "./.prettierrc";
